import React, { Component } from "react";
import {
  Inject,
  ScheduleComponent,
  Day,
  Week,
  Month,
  WorkWeek,
  Agenda,
  ResourceDirective,
  ResourcesDirective,
} from "@syncfusion/ej2-react-schedule";

class Calender extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      members: [],
      Object: [],
    };
  }

  async componentDidMount() {
    await fetch("http://localhost:3000/members")
      .then(async (res) => await res.json())
      .then((json) => {
        this.setState({
          isLoaded: true,
          members: json,
        });
      });
  }

  //localData = members;
  resourceDataSource: Object = [
    { Name: "John", Id: 1, Color: "#ea7a57" },
    { Name: "Steve", Id: 2, Color: "#357CD2" },
  ];

  render() {
    return (
      // <ScheduleComponent>
      //   <Inject services={[Day, Week, Month, Agenda]} />
      // </ScheduleComponent>

      <ScheduleComponent
        height="550px"
        selectedDate={new Date(2020, 4, 8)}
        eventSettings={{ dataSource: this.members }}
      >
        <Inject services={[Day, Week, Month, Agenda]} />
        <ResourcesDirective>
          <ResourceDirective
            field="ResourceID"
            tittle="Resource Name"
            textField="Name"
            idField="Id"
            colorField="Color"
          ></ResourceDirective>
        </ResourcesDirective>
      </ScheduleComponent>
    );
  }
}

export default Calender;
