import React, { Component, useState } from "react";
import { Modal, Row, Col, Form } from "react-bootstrap";
import cal from "./cal.js";
import { Button, ButtonToolbar } from "react-bootstrap";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

class UserActivity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addModalShow: false,
      date: new Date(),
      ActivityDetails: " ",
    };
  }

  // async componentDidMount() {
  //   await fetch("http://localhost:3000/members")
  //     .then(async (res) => await res.json())
  //     .then((json) => {
  //       this.setState({
  //         isLoaded: true,
  //         members: json,
  //       });
  //     });
  // }

  onChange = (date) => this.setState({ date });
  render() {
    let { ActivityDetails } = this.state;
    let addModalClose = () => this.setState({ addModalShow: false });
    return (
      <div>
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              User Activity
            </Modal.Title>
            <div>
              <Calendar onChange={this.onChange} value={this.state.date} />
              {/* {this.state.date.toString()} */}
              {new Intl.DateTimeFormat("en-US", {
                year: "numeric",
                month: "short",
                day: "2-digit",
                hour: "numeric",
                minute: "numeric",
                hour12: true,
              }).format(this.state.date)}
            </div>
          </Modal.Header>
          <Modal.Body>
            <div>{JSON.stringify(this.props.member.activity_periods)}</div>
            <div>
              {this.props.member.filter(
                (itr) =>
                  itr.this.props.member.activity_periods.start_time ==
                  this.state.date
              )}
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button onClick={this.props.onHide}>Close</Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default UserActivity;
