import React, { Component } from "react";
import { Button, ButtonToolbar } from "react-bootstrap";
import UserActivity from "./UserActivity.js";

class UserDashBoard extends Component {
  constructor(props) {
    super(props);

    this.state = {
      members: [],
      isLoaded: false,
      addModalShow: false,
      user_prop: null,
    };
  }

  async componentDidMount() {
    await fetch("http://localhost:3000/members")
      .then(async (res) => await res.json())
      .then((json) => {
        this.setState({
          isLoaded: true,
          members: json,
        });
      });
  }

  render() {
    var { members } = this.state;

    let addModalClose = () => this.setState({ addModalShow: false });

    return (
      <div>
        <div className="toto1">
          <div>
            <h2>User List</h2>
            <div>
              {members.map((member) => (
                <div>
                  <ButtonToolbar
                    show={this.state.addModalShow}
                    onHide={addModalClose}
                  >
                    <Button
                      className="frndbutton"
                      key={member.id}
                      onClick={() =>
                        this.setState({
                          addModalShow: true,
                          user_prop: member.id,
                        })
                      }
                    >
                      {member.real_name}{" "}
                    </Button>
                    <UserActivity
                      show={this.state.addModalShow}
                      onHide={addModalClose}
                      member={member}
                    />
                  </ButtonToolbar>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UserDashBoard;
